import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Main = props => {
    const [pets, setPets] = useState();

    useEffect(() => {
        axios.get("http://localhost:8000/api/pets")
            .then(res => {
                console.log(res.data.pets);
                setPets(res.data.pets)
            })
            .catch(err => console.log(err))
    }, [])

    return(
        <div className="container">
            <a href="/" className="">add a pet to the shelter</a>
            <h2 className="m-4">These pets are looking for a good home</h2>
            <table className = "table table-bordered">
                <tr>
                    <th>Name</th>
                    <th>Type</th>
                    <th>Actions</th>
                </tr>
                {
                    pets ?
                    pets.map((pet, i) => {
                        return <tr>
                            <td>{pet.name}</td>
                            <td>{pet.type}</td>
                            <td><a href="/">Details</a>&nbsp;|&nbsp;<a href="/">Edit</a></td>
                        </tr>
                    }) : ""
                }
            </table>
        </div>
    )
}

export default Main;
